Noritake_VFD_GU3000 vfd;


void import_image(string lang, string num, int storageArea) {
	string sub_path;
	Image image;
	Blob blob;
	const void *blobData;
	unsigned char* byteData;
	//construct path of subtitle BMP
	//TODO: make this relative
	sub_path = "/home/pi/multicaptions/media/images/" + lang + "/" + num + ".bmp";

	//convert BMP to usable format
	image.read(sub_path);
	image.flip();
	image.rotate(-270);
	image.magick("GRAY");

	//convert to byte data
	image.write(&blob);
	blobData = blob.data();
	byteData = static_cast<unsigned char*>(const_cast<void*>(blobData));

	//send image to RAM
	vfd.GU3000_defineImage(RAMImageArea, storageArea, 256, 64, byteData);
}


void print_image(int storageArea) {
	vfd.GU3000_drawImage(RAMImageArea, storageArea, 64, 256, 64);
}


int main(int argc, char **argv) {
	string type, lang, sub_num, sub_num_next;
	const uint8_t blank[2048] = { 0x00 }; //blank image as byte data
	int RAMAddr;

	//probably a better way to do this... Switch?
	if (argc == 1) {
		cout << "Usage: imagetest clear/info/new/next language subtitle_number subtitle_number_next" << endl;
	}
	if (argc > 1) {
		type = argv[1];
		//"clear" = clear screen
		//"info" = meta info (display language)
		//"new" = unexpected sub, change immediately
		//"next" = switch to sub currently in RAM
	}
	if (argc > 2) {
  		lang = argv[2]; //3-character language code
  	}
  	if (argc > 3) {
  		sub_num = argv[3]; //subtitle number
  	}
  	if (argc > 4) {
  		sub_num_next = argv[4]; //next subtitle number (may not be +1)
  	}
  	if (argc > 5) {
  		RAMAddr = atoi(argv[5]);
  	}

	vfd.GU3000_init();
	
	if(type == "new"){
		//immediately clear the screen
		//vfd.GU3000_clearScreen();
		//store and print new subtitle to RAM
		import_image(lang, sub_num, RAMAddr);
		print_image(RAMAddr);
		//then store the next one for fast retrieval
		import_image(lang, sub_num_next, abs(RAMAddr-1));
	} else if(type == "info"){
		//TODO: construct BMPs for info screens
	} else if(type == "clear"){
		vfd.GU3000_clearScreen();
		vfd.GU3000_defineImage(RAMImageArea, 0, 256, 64, blank);
		vfd.GU3000_defineImage(RAMImageArea, 1, 256, 64, blank);
	} else if (type == "next"){
		//print subtitle already in RAM, then store the next one for fast retrieval
		print_image(RAMAddr);
		import_image(lang, sub_num_next, abs(RAMAddr-1));
	}
}


